import { Component, inject, signal } from '@angular/core';
import { JsonPipe } from '@angular/common';
import { RouterOutlet } from '@angular/router';
import { DummyApi, Flight } from 'sdk';

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [JsonPipe, RouterOutlet],
  templateUrl: './app.component.html',
  styleUrl: './app.component.scss'
})
export class AppComponent {
  /** Title of the application */
  public title = 'tutorial-app';

  public readonly dummyApi = inject(DummyApi);

  public readonly flight = signal<Flight | undefined>(undefined);

  constructor() {
    this.loadDummyData();
  }

  async loadDummyData() {
    const dummyData = await this.dummyApi.dummyGet({});
    this.flight.set(dummyData);
  }
}

import { ApiFetchClient } from '@ama-sdk/client-fetch';
import { MockInterceptRequest, SequentialMockAdapter } from '@ama-sdk/core';
import { ApplicationConfig, provideZoneChangeDetection, importProvidersFrom } from '@angular/core';
import { BrowserAnimationsModule } from '@angular/platform-browser/animations';
import { provideRouter } from '@angular/router';
import { prefersReducedMotion } from '@o3r/application';
import { Serializer } from '@o3r/core';
import { ConsoleLogger, Logger, LOGGER_CLIENT_TOKEN, LoggerService } from '@o3r/logger';
import { StorageSync } from '@o3r/store-sync';
import { EffectsModule } from '@ngrx/effects';
import { RuntimeChecks, StoreModule } from '@ngrx/store';
import { DummyApi } from 'sdk';
import { OPERATION_ADAPTER } from 'sdk/spec';
import { routes } from './app.routes';
import { environment, additionalModules } from '../environments/environment';

const localStorageStates: Record<string, Serializer<any>>[] = [/* Store to register in local storage */];
const storageSync = new StorageSync({
  keys: localStorageStates, rehydrate: true
});

const rootReducers = {

};

const metaReducers = [storageSync.localStorageSync()];
const runtimeChecks: Partial<RuntimeChecks> = {
  strictActionImmutability: false,
  strictActionSerializability: false,
  strictActionTypeUniqueness: !environment.production,
  strictActionWithinNgZone: !environment.production,
  strictStateImmutability: !environment.production,
  strictStateSerializability: false
};

function dummyApiFactory(logger: Logger) {
  const apiConfig = new ApiFetchClient(
    {
      basePath: 'http://localhost:3000',
      requestPlugins: [
        new MockInterceptRequest({
          adapter: new SequentialMockAdapter(
            OPERATION_ADAPTER,
            {
              '/dummy_get': [{
                mockData: {
                  originLocationCode: 'PAR',
                  destinationLocationCode: 'NYC'
                }
              }]
            }
          )
        })
      ],
      fetchPlugins: [],
      logger
    }
  );
  return new DummyApi(apiConfig);
}

export const appConfig: ApplicationConfig = {
  providers: [
    provideZoneChangeDetection({ eventCoalescing: true }),
    provideRouter(routes),
    importProvidersFrom(EffectsModule.forRoot([])),
    importProvidersFrom(StoreModule.forRoot(rootReducers, {metaReducers, runtimeChecks})),
    importProvidersFrom(additionalModules),
    importProvidersFrom(BrowserAnimationsModule.withConfig({disableAnimations: prefersReducedMotion()})),
    {provide: LOGGER_CLIENT_TOKEN, useValue: new ConsoleLogger()},
    {provide: DummyApi, useFactory: dummyApiFactory, deps: [LoggerService]}
  ]
};
